import UIKit

// MARK: - Input protocols for current Presenter component VIP-cicle
protocol OrganizationMapShowPresenterInput {
    func didPrepareToShowLocation(responseModel: OrganizationMapShowModels.Location.ResponseModel)
    func didPrepareToDismissViewController(responseModel: OrganizationMapShowModels.Location.ResponseModel)
}

// MARK: - Output protocols for ViewController component VIP-cicle
protocol OrganizationMapShowPresenterOutput: class {
    func didShowLocation(viewModel: OrganizationMapShowModels.Location.ViewModel)
    func didDismissViewController(viewModel: OrganizationMapShowModels.Location.ViewModel)
}

class OrganizationMapShowPresenter: OrganizationMapShowPresenterInput {
    // MARK: - Properties
    weak var viewController: OrganizationMapShowPresenterOutput!
    
    
    // MARK: - Custom Functions. Presentation logic
    func didPrepareToShowLocation(responseModel: OrganizationMapShowModels.Location.ResponseModel) {
        var placeString: String?
        
        if let lines: Array<String> = responseModel.placemark?.addressDictionary?["FormattedAddressLines"] as? Array<String> {
            placeString = lines.joined(separator: ", ")
        }

        let resultLocation = ResultLocation(responseModel.placemark, responseModel.placemark?.location?.coordinate, placeString)
        let viewModel = OrganizationMapShowModels.Location.ViewModel(resultLocation: resultLocation)
        viewController.didShowLocation(viewModel: viewModel)
    }
    
    func didPrepareToDismissViewController(responseModel: OrganizationMapShowModels.Location.ResponseModel) {
        let viewModel = OrganizationMapShowModels.Location.ViewModel(resultLocation: nil)
        viewController.didDismissViewController(viewModel: viewModel)
    }
}
