import UIKit
import SwiftyVK
import FBSDKLoginKit
import GoogleSignIn

// MARK: - Input protocols for current ViewController component VIP-cicle
protocol SignInShowViewControllerInput {
    func displaySomething(viewModel: SignInShowModels.User.ViewModel)
}

// MARK: - Output protocols for Interactor component VIP-cicle
protocol SignInShowViewControllerOutput {
    func didUserSignIn(requestModel: SignInShowModels.User.RequestModel)
}

enum AnimationDirection {
    case FromLeftToRight
    case FromRightToLeft
}

class SignInShowViewController: BaseViewController {
    // MARK: - Properties
    var interactor: SignInShowViewControllerOutput!
    var router: SignInShowRouter!
    var animationDirection: AnimationDirection?
    
    // Managers
    var socialNetworkManager: SocialNetworkManager?
    
    // Container childVC
    var signUpShowVC: SignUpShowViewController?
    var signInContainerShowVC: SignInContainerShowViewController?
    var forgotPasswordShowVC: ForgotPasswordShowViewController?
    var enterCodeShowViewController: EnterCodeShowViewController?
    var repetitionPasswordShowViewController: RepetitionPasswordShowViewController?
    
    var activeViewController: BaseViewController? {
        didSet {
            guard oldValue != nil else {
                router.updateActiveViewController()
                
                return
            }
            
            animationDirection = ((oldValue?.view.tag)! < (activeViewController?.view.tag)!) ? .FromRightToLeft : .FromLeftToRight
            router.removeInactiveViewController(inactiveViewController: oldValue)
        }
    }
    
    // Social network buttons
    @IBOutlet weak var vkontakteButton: CustomButton!
    @IBOutlet weak var googleButton: CustomButton!
    @IBOutlet weak var facebookButton: CustomButton!


    @IBOutlet weak var containerView: UIView!
    @IBOutlet weak var logoBackgroundView: UIView!
    @IBOutlet weak var containerStackView: UIStackView!
    @IBOutlet weak var socialButtonsView: UIView!

    
    // MARK: - Class initialization
    override func awakeFromNib() {
        super.awakeFromNib()
        
        SignInShowConfigurator.sharedInstance.configure(viewController: self)
    }
    

    // MARK: - Class Functions
    override func viewDidLoad() {
        super.viewDidLoad()
        
        doInitialSetupOnLoad()
    }
    

    // MARK: - Custom Functions
    func doInitialSetupOnLoad() {
//        let requestModel = SignInShowModels.User.RequestModel()
//        interactor.doSomething(request: requestModel)
        
        // Hide navigation bar
        hideNavigationBar()
        
        // Apply Container childVC
        (Config.Constants.isUserGuest) ? router.navigateBetweenContainerSubviews() : router.navigateAuthorizedUser(duringStartApp: true)
        
        // Setup App background color theme
        logoBackgroundView.applyBackgroundTheme()
    }
    
    
    // MARK: - Actions
    @IBAction func handlerVkButtonTap(_ sender: CustomButton) {
        print(object: "\(type(of: self)): \(#function) run. Vk button tap.")
        
        if (VK.state == .unknown || VK.state == .configured) {
            socialNetworkManager = SocialNetworkManager(withNetwork: .VK)

            socialNetworkManager!.didAuthorizeUser()
        } else if (VK.state == .authorized) {
            socialNetworkManager?.didLogoutUser()
        }
    }
    
    @IBAction func handlerGoogleButtonTap(_ sender: CustomButton) {
        GIDSignIn.sharedInstance().signIn()
        UIApplication.shared.statusBarStyle = .default
    }
    
    @IBAction func handlerFbButtonTap(_ sender: CustomButton) {
        let facebookLoginManager = FBSDKLoginManager()
        
        UINavigationBar.appearance().barTintColor = UIColor.red
        facebookLoginManager.logIn(withReadPermissions: ["public_profile", "email"], from: self) { (result, error) in
            // Done
            guard result?.token != nil else {
                return
            }
            
            // Return values
            let fbToken = result!.token.tokenString
            let fbUserID = result!.token.userID
            
//            let fbUserEmail = result!.value(forKey: "e-mail") as! String
//            let strFirstName = result!.value(forKey: "first_name") as! String
//            let strLastName = result!.value(forKey: "last_name") as! String
//            let strPictureURL = result!.value(forKey: "picture")value(forKey: "data")value(forKey: "url") as! String
            
            Config.Constants.isUserGuest = false
            self.router.navigateAuthorizedUser(duringStartApp: false)
        }
    }

    
    // MARK: - Gesture
    @IBAction func handlerTapGestureRecognizer(_ sender: UITapGestureRecognizer) {
        view.endEditing(true)
    }
}


